import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class EditExercise extends Component {
  constructor(props) {
    super(props);

    //Bind
    this.onChangeClientName=this.onChangeClientName.bind(this);
    this.onChangeClientPhone=this.onChangeClientPhone.bind(this);
    this.onChangeClientEmail=this.onChangeClientEmail.bind(this);
    this.onChangeClientCompany=this.onChangeClientCompany.bind(this);
    this.onChangeClientRole=this.onChangeClientRole.bind(this);
    this.onChangeClientDescription=this.onChangeClientDescription.bind(this);
    this.onSubmit=this.onSubmit.bind(this);

    this.state={
        name:'',
        phone:'',
        email:'',
        company:'',
        role:'',
        description:''
        }  
  }

  componentDidMount() {
    axios.get('http://localhost:5000/users'+this.props.match.params.id)
      .then(response => {
        this.setState({
             name: response.data.name,
            phone: response.data.phone,
            email: response.data.email,
            company: response.data.company,
            role:response.data.company,
            description:response.data.company
        })  
    })
      .catch(function (error) {
        console.log(error);
      })

    axios.get('http://localhost:5000/users')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.name),
            phone:response.data.map(user=>user.phone),
            email:response.data.map(user=>user.email),
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })
  }

  onChangeDuration(e) {
    this.setState({
      duration: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const exercise = {
      username: this.state.username,
      description: this.state.description,
      duration: this.state.duration,
      date: this.state.date
    }

    console.log(exercise);

    axios.post('http://localhost:5000/exercises/update/' + this.props.match.params.id, exercise)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Edit Exercise Log</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Username: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}>
              {
                this.state.users.map(function(user) {
                  return <option 
                    key={user}
                    value={user}>{user}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
          <label>Description: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
              />
        </div>
        <div className="form-group">
          <label>Duration (in minutes): </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.duration}
              onChange={this.onChangeDuration}
              />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Edit Exercise Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
