import React, { Component } from 'react';
import axios from 'axios';

export default  class Registration extends Component{
    constructor(props){
        super(props);
        //Bind
        this.onChangeClientName=this.onChangeClientName.bind(this);
        this.onChangeClientPhone=this.onChangeClientPhone.bind(this);
        this.onChangeClientEmail=this.onChangeClientEmail.bind(this);
        this.onChangeClientCompany=this.onChangeClientCompany.bind(this);
        this.onChangeClientRole=this.onChangeClientRole.bind(this);
        this.onChangeClientDescription=this.onChangeClientDescription.bind(this);
        this.onSubmit=this.onSubmit.bind(this);

        //Constructor
        this.state={
        name:'',
        phone:'',
        email:'',
        company:'',
        role:'',
        description:''
        }   
        
        this.setState({
            name:'',
             phone:'',
             email:'',
             company:'',
             role:'',
             description:'' 

         })
    }
    componentDidMount() {
        axios.get('http://localhost:5000/users/')
          .then(response => {
            if (response.data.length > 0) {
              this.setState({
                users: response.data.map(user => user.name), 
                name: response.data[0].name
              })
            }
          })
          .catch((error) => {
            console.log(error);
          })
    }
    
    onChangeClientName(e){
        this.setState({
            name:e.target.value
            
        });
    }
    
    onChangeClientPhone(e){
        this.setState({
            phone:e.target.value
        });
    }

    onChangeClientEmail(e){
        this.setState({
            email:e.target.value
        });
    }

    onChangeClientCompany(e){
        this.setState({
            company:e.target.value
        });
    }

    onChangeClientRole(e){
        this.setState({
            role:e.target.value
        });
    }

    onChangeClientDescription(e){
        this.setState({
            description:e.target.value
        });
    }

     onSubmit(e){
        e.preventDefault();
        
        const client = {
            name: this.state.name,
            phone:this.state.phone,
            email:this.state.email,
            company:this.state.company,
            role:this.state.role,
            description:this.state.description
        } 
        Console.log(client);
        window.location='/edit';
        axios.post('http://localhost:5000/users/add', client)
        .then(res => console.log(res.data));

        
        
    }

    render(){
        return(
        <div>
            <h3>Registration</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 

                        <label>Name: </label>
                        <input  type="text"required className="form-control" value={this.state.name} onChange={this.onChangeClientName}/>
                    
                        
                        <label>Phone: </label>
                        <input  type="number" required className="form-control" value={this.state.phone} onChange={this.onChangeClientPhone}/> 


                        <label>Email: </label>
                        <input  type="email"required className="form-control" value={this.state.email} onChange={this.onChangeClientEmail}/>

                        <label>Company: </label>
                        <input  type="text" className="form-control" value={this.state.company} onChange={this.onChangeClientCompany}/>

                        <label>Role: </label>
                        <input  type="text"className="form-control" value={this.state.role} onChange={this.onChangeClientRole}/>

                        <label>Description: </label>
                        <input  type="text"className="form-control" value={this.state.description} onChange={this.onChangeClientDescription}/>
                    </div>
                   
                        <div className="form-group">
                        <div className="form-group"> <input type="submit" value="Register" className="btn btn-primary" /></div>
                        </div>
                
                </form>
            </div>
        )
    }
}
