import React, { Component } from 'react';

export default  class Registration extends Component{
    constructor(props){
        super(props);
        
        this.onChangeClientName=this.onChangeClientName.bind(this);
        this.onChangeClientPhone=this.onChangeClientPhone.bind(this);
        this.onChangeClientEmail=this.onChangeClientEmail.bind(this);
        this.onChangeClientCompany=this.onChangeClientCompany.bind(this);
        this.onChangeClientRole=this.onChangeClientRole.bind(this);
        this.onChangeClientDescription=this.onChangeClientDescription.bind(this);
        this.onSubmit=this.onSubmit.bind(this);

        
        this.state={
                name:'',
                phone:'',
                email:'',
                company:'',
                role:'',
                description:''
        }  
    }

    onChangeClientName(e){
        this.setState({
            name:e.target.value
        });
    }

    onChangeClientPhone(e){
        this.setState({
            phome:e.target.value
        });
    }

    onChangeClientEmail(e){
        this.setState({
            email:e.target.value
        });
    }

    onChangeClientCompany(e){
        this.setState({
            company:e.target.value
        });
    }

    onChangeClientRole(e){
        this.setState({
            role:e.target.value
        });
    }

    onChangeClientDescription(e){
        this.setState({
            description:e.target.value
        });
    }

    onSubmit(e){
        e.preventDefault();

        const client={
            name:this.state.name,
            phone:this.state.phone,
            email:this.state.email,
            company:this.company.name,
            role:this.state.role,
            description:this.state.description
        }
        
        console.log(client);
        /* axios.post('http://localhost:5000/users/add', client).then(res => console.log(res.data));
 */
        this.setState({
            name: '',
            phone:'',
            email:'',
            company:'',
            role:'',
            description:''
        })
    }

    render(){
        return(
        <div>
            <h3>Create New User</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                    <label>Name: </label>
                    <input  type="text"required className="form-control" value={this.state.name} onChange={this.onChangeClientName}/>
                    <input  type="text"required className="form-control" value={this.state.phone} onChange={this.onChangeClientPhone}/>
                    <input  type="text"required className="form-control" value={this.state.email} onChange={this.onChangeClientEmail}/>
                    <input  type="text"required className="form-control" value={this.state.company} onChange={this.onChangeClientCompany}/>
                    <input  type="text"required className="form-control" value={this.state.role} onChange={this.onChangeClientRole}/>
                    <input  type="text"required className="form-control" value={this.state.description} onChange={this.onChangeClientDescription}/>
                    </div>

                    <div className="form-group"> <input type="submit" value="Register" className="btn btn-primary" /></div>
                </form>
        </div>
        )
    }
}
