import React, { useState} from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';



const Registration = () => {
    const api = "http://localhost:5000/users/add";
    const history = useHistory();
    const [client, setClient] = useState({
        name: "",
        phone: "",
        email: "",
        company: "",
        role: "",
        description: ""
    })

    const onChangeClientName = (e) => {
        setClient({...client, name: e.target.value});
    }
    const onChangeClientPhone= (e) => {
        setClient({...client, phone: e.target.value});
    }
    const onChangeClientEmail= (e) => {
        setClient({...client, email: e.target.value});
    }
    const onChangeClientCompany= (e) => {
        setClient({...client, company: e.target.value});
    }
    const onChangeClientRole= (e) => {
        setClient({...client, role: e.target.value});
    }
    const onChangeClientDescription= (e) => {
        setClient({...client, description: e.target.value});
    }
    
    const submitHandler = (e) => {
        e.preventDefault();
        axios.post(api, {
            client
        })
        .then((res) =>{
            console.log(res.data);
            history.push('/');
        })
    }

    return(
        <>
        <div>
            <h3>Registration</h3>
                <form onSubmit={submitHandler}>
                    <div className="form-group"> 

                        <label>Name: </label>
                        <input  type="text" required className="form-control" value={client.name} onChange={onChangeClientName}/>
                    
                        
                        <label>Phone: </label>
                        <input  type="string" required className="form-control" value={client.phone} onChange={onChangeClientPhone}/> 


                        <label>Email: </label>
                        <input  type="email"required className="form-control" value={client.email} onChange={onChangeClientEmail}/>

                        <label>Company: </label>
                        <input  type="text" className="form-control" value={client.company} onChange={onChangeClientCompany}/>

                        <label>Role: </label>
                        <input  type="text"className="form-control" value={client.role} onChange={onChangeClientRole}/>

                        <label>Description: </label>
                        <input  type="text"className="form-control" value={client.description} onChange={onChangeClientDescription}/>
                    </div>                        
                        <div className="form-group"> <input type="submit" value="Register" className="btn btn-primary" /></div>                
                </form>
            </div>
        </>
    )

};

export default Registration;
